import React, { useState, useEffect } from 'react';
import Calendarios from './Calendarios';
import DayOffcanvas from './DayOffcanvas';
import { getColombianHolidays } from './holidays';
import '../css/Home.css';

function Home() {
  const [monthCalendario, setMonthCalendario] = useState(new Date().getMonth());
  const [yearSet, setYearSet] = useState(new Date().getFullYear());
  const [colombianHolidays, setColombianHolidays] = useState([]);

  const [selectedDay, setSelectedDay] = useState(null); 
  const [shiftData, setShiftData] = useState({}); 

  useEffect(() => {
    const fetchHolidays = async () => {
      const holidays = await getColombianHolidays(yearSet);
      setColombianHolidays(holidays);
    };
    fetchHolidays();
  }, [yearSet]);

  const handleDayClick = (day, month, year) => {
    setSelectedDay({ day, month, year });
  };

  const handleCloseOffcanvas = () => {
    setSelectedDay(null);
  };

  const handleUpdateShifts = (dateString, updatedShifts) => {
    setShiftData(prev => ({
      ...prev,
      [dateString]: updatedShifts,
    }));
  };

  const getShiftsForDay = (day, month, year) => {
    const dateString = `${year}-${month}-${day}`;
    return shiftData[dateString] || { morning: [], afternoon: [], night: [], off: [] };
  };

  return (
    <div className="home-container">
       <div className="controls-container">
        {/* Aquí puedes agregar selectores para año y mes si lo deseas */}
      </div>

      <Calendarios
        monthCalendario={monthCalendario}
        yearSet={yearSet}
        setMonthCalendario={setMonthCalendario}
        colombianHolidays={colombianHolidays}
        onDayClick={handleDayClick} 
      />

      {selectedDay && (
        <DayOffcanvas
          day={selectedDay.day}
          month={selectedDay.month}
          year={selectedDay.year}
          onClose={handleCloseOffcanvas}
          shifts={getShiftsForDay(selectedDay.day, selectedDay.month, selectedDay.year)}
          onUpdateShifts={(updatedShifts) => handleUpdateShifts(
            `${selectedDay.year}-${selectedDay.month}-${selectedDay.day}`,
            updatedShifts
          )}
        />
      )}
    </div>
  );
}

export default Home;
