export const formatNameToShort = (fullName) => {
  if (!fullName || typeof fullName !== 'string') {
    return '';
  }

  // "Juan Perez Rodriguez" -> ["juan", "perez", "rodriguez"]
  const parts = fullName.trim().toLowerCase().split(' ');

  // Si no hay un apellido, devuelve el nombre tal cual (ej. "Admin")
  if (parts.length < 2) {
    return parts[0] || '';
  }

  const firstNameInitial = parts[0].charAt(0);
  const firstLastName = parts[1]; // Tomamos solo el primer apellido

  return `${firstNameInitial}${firstLastName}`;
};
