import React, { useState } from 'react';
import { useCalendar } from '../context/CalendarContext';
import '../css/PeopleManager.css';

function PeopleManager() {
  const { people, savePerson, deletePerson } = useCalendar();
  const [newPersonName, setNewPersonName] = useState('');
  const [newPersonCargo, setNewPersonCargo] = useState('');

  const handleAddPerson = () => {
    if (newPersonName.trim() && newPersonCargo.trim()) {
      // savePerson crea una persona si no se le pasa un ID
      savePerson({
        name: newPersonName,
        cargo: newPersonCargo,
      });
      setNewPersonName('');
      setNewPersonCargo('');
    } else {
      alert('Por favor, ingresa el nombre y el cargo de la persona.');
    }
  };

  const handleDeletePerson = (personId) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar a esta persona? Esto también la eliminará de todos los turnos asignados.')) {
        deletePerson(personId);
    }
  }

  return (
    <div className="people-manager">
      <h2>Gestión de Personal</h2>

      <div className="add-person-form">
        <input
          type="text"
          placeholder="Nombre de la persona"
          value={newPersonName}
          onChange={(e) => setNewPersonName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Cargo"
          value={newPersonCargo}
          onChange={(e) => setNewPersonCargo(e.target.value)}
        />
        <button onClick={handleAddPerson}>Añadir Persona</button>
      </div>

      <div className="people-list">
        {people.map(person => (
          <div key={person.id} className="person-row">
            <div className="person-info">
              <span className="person-name">{person.name}</span>
              <span className="person-role">{person.cargo}</span>
            </div>
            <button className="delete-btn" onClick={() => handleDeletePerson(person.id)}>Eliminar</button>
          </div>
        ))}
        {people.length === 0 && <p className="no-people">No hay personal registrado.</p>}
      </div>
    </div>
  );
}

export default PeopleManager;
