import React from 'react';
import PropTypes from 'prop-types';
import { shiftColors } from '../../utils/shiftColors';

// A memoized helper function to avoid recalculating initials on every render.
const getInitials = React.memo((name) => {
  if (!name) return '';
  const names = name.trim().split(' ');
  const firstName = names[0] || '';

  if (names.length === 1) {
    return firstName.substring(0, 2).toUpperCase();
  }

  const lastName = names[names.length - 1] || '';
  const firstInitial = firstName[0] || '';
  const lastInitial = lastName[0] || '';
  let middleInitial = '';
  if (firstName.length > 2) {
    middleInitial = firstName[2];
  } 

  return (firstInitial + middleInitial + lastInitial).toUpperCase();
});

/**
 * PersonBubble Component
 * Displays a colored circle with the person's initials.
 * It's memoized for performance, as it can be rendered many times.
 */
const PersonBubble = React.memo(({ person, shift, onClick }) => {
  const initials = getInitials(person.name);
  const shiftTitle = shift.charAt(0).toUpperCase() + shift.slice(1); // Capitalize shift name

  return (
    <div 
      className="person-bubble"
      title={`${person.name} (${shiftTitle})`}
      style={{ backgroundColor: shiftColors[shift] }}
      onClick={() => onClick(person)} // Allow interaction, e.g., opening a profile
    >
      {initials}
    </div>
  );
});

PersonBubble.propTypes = {
  person: PropTypes.shape({
    id: PropTypes.oneOfType([PropTypes.string, PropTypes.number]).isRequired,
    name: PropTypes.string.isRequired,
  }).isRequired,
  shift: PropTypes.string.isRequired,
  onClick: PropTypes.func,
};

PersonBubble.defaultProps = {
  onClick: () => {},
};

export default PersonBubble;
