import React from 'react';
import { useCalendar } from '../context/CalendarContext.js';

function ProgrammingCalendar({ date }) {
  const { people, holidays, assignedDays } = useCalendar();
  const year = date.getFullYear();
  const month = date.getMonth();

  const getDaysInMonth = (year, month) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (year, month) => {
    return new Date(year, month, 1).getDay();
  };

  const daysInMonth = getDaysInMonth(year, month);
  const firstDay = getFirstDayOfMonth(year, month);

  const blanks = Array(firstDay).fill(null);
  const days = Array.from({ length: daysInMonth }, (_, i) => i + 1);

  const isHoliday = (day) => {
    const holiday = holidays.find(h => h.day === day && h.month - 1 === month);
    return holiday ? 'holiday' : '';
  };

  const getPeopleForDay = (day) => {
    const key = `${year}-${month + 1}-${day}`;
    const dayAssignments = assignedDays[key] || {};
    return Object.keys(dayAssignments).map(personId => {
        const person = people.find(p => p.id.toString() === personId);
        return person ? person.name : null;
    }).filter(name => name !== null);
  };

  return (
    <div className="programming-calendar-grid">
      <div className="day-header">D</div>
      <div className="day-header">L</div>
      <div className="day-header">M</div>
      <div className="day-header">M</div>
      <div className="day-header">J</div>
      <div className="day-header">V</div>
      <div className="day-header">S</div>

      {blanks.map((_, i) => <div key={`blank-${i}`} className="day-cell empty"></div>)}

      {days.map(day => {
        const peopleOnDay = getPeopleForDay(day);
        return (
          <div key={day} className={`day-cell ${isHoliday(day)}`}>
            <div className="day-number">{day}</div>
            <div className="day-people">
              {peopleOnDay.map((name, i) => <div key={i} className="person-name">{name}</div>)}
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default ProgrammingCalendar;
