import { getISOWeek } from 'date-fns';

import { getMondayNumbers } from './getMondayNumbers';

function WeekCount({ monthCalendario, yearSet, setWeekNumber }) {
  const day = [
    1,
    ...getMondayNumbers(monthCalendario, yearSet).filter(
      (number) => number !== 1
    ),
  ];

  const handleClick = (selectedWeek) => {
    setWeekNumber(selectedWeek);
  };

  const weekNumbers = day.map((dayItem) => {
    const date = new Date(yearSet, monthCalendario, dayItem);
    const weekNumber = getISOWeek(date);

    const firstDayOfMonth = new Date(yearSet, monthCalendario, 1);
    const dayOfWeek = (firstDayOfMonth.getDay() + 6) % 7; // Lunes = 0, Domingo = 6

    // Calcula en qué fila de la cuadrícula debe aparecer el número de la semana
    const gridRowStart = Math.floor((dayItem + dayOfWeek - 1) / 7) + 2;

    return (
      <li
        className="week-number"
        style={{ gridRowStart: gridRowStart }}
        key={weekNumber + '-' + monthCalendario}
        onClick={() => handleClick(weekNumber)}
        data-bs-toggle="offcanvas"
        data-bs-target="#offcanvasWithBothOptions"
        aria-controls="offcanvasWithBothOptions"
      >
        {weekNumber}
      </li>
    );
  });

  return <>{weekNumbers}</>;
}

export default WeekCount;
