import React, { useContext } from 'react';
import WeekDetail from './WeekDetail.js';
import { getMonthsTitule } from '../utils/getMonthsTitule.js';
import { useCalendar } from '../context/CalendarContext.js';
import { ProfileContext } from '../context/ProfileContext';

function PersonOffCanvas() { 
  const { 
    selectedWeek, 
    monthCalendario, 
    people,
    programmedSchedule,
    temporarySchedule,
    saveTemporarySchedule
  } = useCalendar();
  const { profile } = useContext(ProfileContext);

  const months = getMonthsTitule();
  const monthName = months[monthCalendario];

  // Comprobar si hay cambios pendientes
  const hasChanges = JSON.stringify(programmedSchedule) !== JSON.stringify(temporarySchedule);

  return (
    <div
      className="offcanvas offcanvas-start offcanvas-wide"
      data-bs-scroll="true"
      id="offcanvasWithBothOptions"
      aria-labelledby="offcanvasWithBothOptionsLabel"
    >
      <div className="offcanvas-header">
        <h4 className='monthToday' id='monthToday'>{monthName}</h4>
        <h5 className="offcanvas-title" id="offcanvasWithBothOptionsLabel">
          {selectedWeek ? `Semana ${selectedWeek}` : 'Detalles de la Semana'}
        </h5>

        {/* Bot√≥n para guardar cambios (visible para ADM y si hay cambios) */}
        {profile?.role === 'ADM' && hasChanges && (
          <button onClick={saveTemporarySchedule} className="btn btn-success ms-auto">
            Guardar Cambios
          </button>
        )}

        <button
          type="button"
          className="btn-close"
          data-bs-dismiss="offcanvas"
          aria-label="Close"
        ></button>
      </div>
      <div className="offcanvas-body">
       <WeekDetail people={people} /> 
      </div>
    </div>
  );
}

export default PersonOffCanvas;
