import React, { useContext } from 'react';
import DayList from './DayList.js';
import useCalendarStore from '../store/calendarStore'; // Import the new Zustand store
import { ProfileContext } from '../context/ProfileContext.js';
import '../css/WeekDetail.css';

function WeekDetail() { 
  // Get people data from the new store
  const { people } = useCalendarStore(); 
  const { profile } = useContext(ProfileContext);

  // The concepts of 'selectedWeek', 'isDirty', and 'saveTemporarySchedule' are deprecated.
  // The new calendar view handles its state directly, and the store autosaves.
  // The DayList component is also deprecated.

  return (
    <div>
      {/* 
        The save button is no longer needed as changes are saved automatically.
        The DayList component is deprecated and will render null.
      */}
      <DayList people={people} />
    </div>
  );
}

export default WeekDetail;
