import React from 'react';
import './App.css';

import Login from './components/Login.js';
import CardsCald from './components/CardsCald.js';
import Header from './components/Header.js';
import Home from './components/Home.js';
import CardProfile from './components/CardProfile.js';
import PersonOffCanvas from './components/PersonOffCanvas.js';
import { Routes, Route, BrowserRouter } from 'react-router-dom';
import { CalendarProvider } from './context/CalendarContext';

function App() {
  // Ya no hay estado aquí. App.js es ahora un componente "presentacional" y de enrutamiento.

  return (
    <div className="App">
      <BrowserRouter>
        <Header />
        {/* El Provider envuelve a toda la aplicación, 
            dando acceso global al contexto */}
        <CalendarProvider>
          <Routes>
            <Route exact path="/" element={<Login />} key="login"></Route>
            <Route exact path="/Home" element={<Home />} key="Home"></Route>
            <Route 
              exact 
              path="/Calendario" 
              element={<CardsCald />} // Sin props
              key="cardcalendario">
            </Route>
            <Route
              exact
              path="/Perfiles"
              // Ya no se pasan props, CardProfile usará el contexto directamente.
              element={<CardProfile />}
              key="profiles"
            ></Route>
          </Routes>

          {/* PersonOffCanvas también usará el contexto */}
          <PersonOffCanvas />

        </CalendarProvider>
      </BrowserRouter>
    </div>
  );
}

export default App;
