import React from 'react';
import WeekDetail from './WeekDetail.js';
import useCalendarStore from '../store/calendarStore'; // Import the new Zustand store

function PersonOffCanvas() { 
  // Get people data from the new store
  const { people } = useCalendarStore(); 

  // The concepts of 'selectedWeek' and 'monthCalendario' are deprecated.
  // The title will be generic now.

  return (
    <div
      className="offcanvas offcanvas-start offcanvas-wide"
      data-bs-scroll="true"
      id="offcanvasWithBothOptions"
      aria-labelledby="offcanvasWithBothOptionsLabel"
    >
      <div className="offcanvas-header">
        <h4 className='monthToday' id='monthToday'>Detalles</h4>
        <h5 className="offcanvas-title" id="offcanvasWithBothOptionsLabel">
          Detalles de la Semana
        </h5>
        <button
          type="button"
          className="btn-close"
          data-bs-dismiss="offcanvas"
          aria-label="Close"
        ></button>
      </div>
      <div className="offcanvas-body">
       {/* WeekDetail is deprecated and will render null */}
       <WeekDetail people={people} /> 
      </div>
    </div>
  );
}

export default PersonOffCanvas;
