import React, { useMemo } from 'react';
import PropTypes from 'prop-types';
import PersonBubble from './PersonBubble';

/**
 * DayCell Component
 * Renders a single day in the calendar grid.
 * It displays the day number and the PersonBubbles for each shift.
 */
const DayCell = React.memo(({ day, shiftsForDay, isHoliday, onPersonClick }) => {

  // Memoize the flattened list of shifts to avoid re-calculating on every render.
  const shiftItems = useMemo(() => {
    if (!shiftsForDay) return [];
    // Transform the shift object into a flat array for easier mapping.
    return Object.entries(shiftsForDay).flatMap(([shift, people]) => 
      people.map(person => ({ person, shift }))
    );
  }, [shiftsForDay]);

  const hasShifts = shiftItems.length > 0;

  return (
    <div className={`day-cell-pro ${isHoliday ? 'holiday' : ''} ${!hasShifts ? 'empty-day' : ''}`}>
      <div className="day-number-pro">{day}</div>

      {hasShifts && (
        <div className="people-list-pro">
          {shiftItems.map(({ person, shift }) => (
            <PersonBubble 
              key={`${person.id}-${shift}`}
              person={person}
              shift={shift}
              onClick={onPersonClick}
            />
          ))}
        </div>
      )}
    </div>
  );
});

DayCell.propTypes = {
  day: PropTypes.number.isRequired,
  shiftsForDay: PropTypes.object, // Can be null or undefined
  isHoliday: PropTypes.bool,
  onPersonClick: PropTypes.func,
};

DayCell.defaultProps = {
  shiftsForDay: null,
  isHoliday: false,
  onPersonClick: () => {},
};

export default DayCell;
