import React, { useState, useContext } from 'react';
import '../css/WeekDetail.css';
import AssignPersonModal from './AssignPersonModal.js';
import useCalendarStore from '../store/calendarStore'; // Import the new Zustand store
import { ProfileContext } from '../context/ProfileContext.js';

// ShiftSection remains the same as it's a presentational component.
const ShiftSection = ({ title, people, onAdd, onRemove, profile }) => {
  const finalShortNames = people.reduce((acc, person) => {
    if (!person.name) {
      acc[person.id] = '';
      return acc;
    }
    const nameParts = person.name.toLowerCase().split(' ').filter(Boolean);
    if (nameParts.length === 0) {
      acc[person.id] = '';
      return acc;
    }
    const firstName = nameParts[0];
    const lastName = nameParts.length > 1 ? nameParts[nameParts.length - 1] : '';
    let shortName = `${firstName.charAt(0)}${lastName ? '.' + lastName : ''}`;
    let prefixLength = 1;
    while (Object.values(acc).includes(shortName) && firstName.length > prefixLength) {
      prefixLength++;
      shortName = `${firstName.substring(0, prefixLength)}${lastName ? '.' + lastName : ''}`;
    }
    if (Object.values(acc).includes(shortName)) {
        let count = 2;
        let newShortName = `${shortName}${count}`;
        while(Object.values(acc).includes(newShortName)){
            count++;
            newShortName = `${shortName}${count}`;
        }
        shortName = newShortName;
    }
    acc[person.id] = shortName;
    return acc;
  }, {});

  return (
    <div className="shift-section">
      <h6 className="shift-title">{title}</h6>
      <div className="people-list">
        {people.map(person => (
          <span key={person.id} className="person-pill" title={person.name}>
            {finalShortNames[person.id]}
            {profile?.role === 'ADM' && <button onClick={() => onRemove(person.id)} className="remove-person-btn">X</button>}
          </span>
        ))}
      </div>
      {profile?.role === 'ADM' && <button onClick={onAdd} className="add-person-btn">+</button>}
    </div>
  );
};

function DayCard({ day, weekDays }) { // removed `people` prop as it's now from global state
  // 1. Get state and actions from the Zustand store
  const {
    holidays,
    schedule,
    people,
    assignShift,
    removeShift,
  } = useCalendarStore();
  const { profile } = useContext(ProfileContext);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedShift, setSelectedShift] = useState(null);
  const [validPeople, setValidPeople] = useState([]);

  // Helper to format Date object to 'YYYY-MM-DD' string
  const getDayString = (d) => d.toISOString().split('T')[0];

  const dayString = getDayString(day);
  const dayShifts = schedule[dayString] || { morning: [], afternoon: [], night: [], off: [] };

  const dayOfMonth = day.getDate();
  const month = day.getMonth() + 1;
  const isHoliday = holidays.some(h => h.dia === dayOfMonth && h.mes === month);
  const dayName = day.toLocaleDateString('es-ES', { weekday: 'short' });

  const cardStyle = { ...(isHoliday && { backgroundColor: '#fdd' }), listStyle: 'none' };
  const dayNumberStyle = { ...(isHoliday && { color: 'red', fontWeight: 'bold' }) };

  // 2. Re-implement getValidPeopleForShift logic inside the component
  const getValidPeopleForShift = (dateForValidation) => {
    const dateStr = getDayString(dateForValidation);
    const shiftsOnDay = schedule[dateStr];
    if (!shiftsOnDay) return people; // If no one is working, everyone is available

    const assignedPeopleIds = new Set(Object.values(shiftsOnDay).flat().map(p => p.id));
    return people.filter(p => !assignedPeopleIds.has(p.id));
  };

  const shiftTypes = [
    { id: 'morning', title: 'Mañana' },
    { id: 'afternoon', title: 'Tarde' },
    { id: 'night', title: 'Noche' },
    { id: 'off', title: 'Libre' },
  ];

  const handleAddPerson = (shiftType) => {
    const validPeopleForShift = getValidPeopleForShift(day);
    setValidPeople(validPeopleForShift);
    setSelectedShift(shiftType);
    setIsModalOpen(true);
  };

  // 3. Adapt handleAssignToDays to loop and call the new store action
  const handleAssignToDays = (person, selectedDays) => {
    selectedDays.forEach(d => {
      const dateString = getDayString(d);
      // Use the new assignShift from the store
      assignShift(dateString, selectedShift, person.id);
    });
    setIsModalOpen(false);
  };

  // 4. Adapt handleRemovePerson to the new store action
  const handleRemovePerson = (shiftType, personId) => {
    removeShift(dayString, shiftType, personId);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="day-card" style={cardStyle}>
      <div className="day-header">
        <div className="day-name">{dayName}</div>
        <div className="day-number" style={dayNumberStyle}>{dayOfMonth}</div>
      </div>
      <div className="shifts-container">
        {shiftTypes.map((shift) => (
          <ShiftSection
            key={shift.id}
            title={shift.title}
            people={dayShifts[shift.id]}
            onAdd={() => handleAddPerson(shift.id)}
            onRemove={(personId) => handleRemovePerson(shift.id, personId)}
            profile={profile}
          />
        ))}
      </div>

      {isModalOpen && (
        <AssignPersonModal
          people={validPeople}
          onAssign={handleAssignToDays}
          onClose={handleCloseModal}
          initialDay={day}
          weekDays={weekDays}
          shiftType={selectedShift}
        />
      )}
    </div>
  );
}

export default DayCard;
