import React from 'react';
import DaysCalendar from './DaysCalendar';
import WeekCount from './WeekCount';

function Calendarios({ monthCalendario, yearSet, setWeekNumber }) {
  const getDaysInMonth = (year, month) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const daysInMonth = getDaysInMonth(yearSet, monthCalendario);
  const firstDayOfMonth = new Date(yearSet, monthCalendario, 1).getDay();

  // Días de la semana
  const weekdays = ['Lun', 'Mar', 'Mié', 'Jue', 'Vie', 'Sáb', 'Dom'];

  return (
    <>
      <WeekCount month={monthCalendario} year={yearSet} />
      <div
        className='calendar'
        style={{ '--day-start': firstDayOfMonth === 0 ? 7 : firstDayOfMonth }}
      >
        <ul className='weekdays'>
          {weekdays.map((day) => (
            <li key={day} className='day-name'>
              {day}
            </li>
          ))}
        </ul>
        <ul className='days'>
          {Array.from({ length: daysInMonth }, (_, i) => i + 1).map((day) => (
            <DaysCalendar
              key={day}
              day={day}
              month={monthCalendario}
              year={yearSet}
              setWeekNumber={setWeekNumber}
              isFirstDay={day === 1}
            />
          ))}
        </ul>
      </div>
    </>
  );
}

export default Calendarios;
