import React, { useState } from 'react';
import '../css/AssignPersonModal.css';

function AssignPersonModal({ people, onSelect, onClose, existingPeopleIds }) {
  const [searchTerm, setSearchTerm] = useState('');

  const availablePeople = people.filter(p => !existingPeopleIds.includes(p.id));

  const filteredPeople = availablePeople.filter(person =>
    person.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="modal-overlay">
      <div className="modal-content assign-modal">
        <h2>Asignar Persona</h2>
        <input
          type="text"
          placeholder="Buscar persona..."
          className="search-input-modal"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <div className="person-selection-list">
          {filteredPeople.length > 0 ? (
            filteredPeople.map(person => (
              <div
                key={person.id}
                className="person-selection-item"
                onClick={() => onSelect(person)}
              >
                {person.name}
              </div>
            ))
          ) : (
            <p>No hay personas disponibles para asignar.</p>
          )}
        </div>
        <div className="form-actions">
          <button type="button" className="btn-cancel" onClick={onClose}>
            Cancelar
          </button>
        </div>
      </div>
    </div>
  );
}

export default AssignPersonModal;
