import React, { useState } from 'react';
import '../css/Profile.css';
import EditProfileForm from './EditProfileForm';

const API_URL = 'http://localhost:5000/people';

function CardProfile({ people, setPeople }) {
  const [searchTerm, setSearchTerm] = useState('');
  const [editingPerson, setEditingPerson] = useState(null);
  const [isCreating, setIsCreating] = useState(false);

  const handleEdit = (person) => {
    setEditingPerson(person);
  };

  const handleOpenCreate = () => {
    setIsCreating(true);
  };

  const handleSave = async (formData) => {
    try {
      if (formData.id) {
        // Editar persona
        const response = await fetch(`${API_URL}/${formData.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData),
        });
        if (!response.ok) throw new Error('Failed to update person.');
        const updatedPerson = await response.json();
        setPeople(people.map(p => p.id === updatedPerson.id ? updatedPerson : p));
      } else {
        // Crear persona
        const response = await fetch(API_URL, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData),
        });
        if (!response.ok) throw new Error('Failed to create person.');
        const newPerson = await response.json();
        setPeople([...people, newPerson]);
      }
    } catch (error) {
      console.error('Error saving person:', error);
    }
    handleClose();
  };

  const handleDelete = async (personId) => {
    try {
      const response = await fetch(`${API_URL}/${personId}`, {
        method: 'DELETE',
      });
      if (!response.ok) throw new Error('Failed to delete person.');
      setPeople(people.filter(p => p.id !== personId));
    } catch (error) {
      console.error('Error deleting person:', error);
    }
  };

  const handleClose = () => {
    setEditingPerson(null);
    setIsCreating(false);
  };

  const filteredPersons = Array.isArray(people)
    ? people.filter((person) =>
        person.name.toLowerCase().includes(searchTerm.toLowerCase())
      )
    : [];

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <button className="btn-create" onClick={handleOpenCreate}>Crear Perfil</button>
        <div className="search-bar">
          <input
            type="text"
            className="search-input"
            placeholder="Buscar por nombre..."
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </div>
      </div>

      <div className="profile-grid">
        {filteredPersons.map((person) => (
          <div className="profile-card" key={person.id}>
            <div className="profile-card-header">
              <h5 className="profile-name">{person.name}</h5>
            </div>
            <div className="profile-card-body">
              <p><strong>Identificación:</strong> {person.id}</p>
              <p><strong>Cargo:</strong> {person.cargo}</p>
            </div>
            <div className="profile-card-footer">
              <button className="btn-edit" onClick={() => handleEdit(person)}>Editar</button>
              <button className="btn-delete" onClick={() => handleDelete(person.id)}>Eliminar</button>
            </div>
          </div>
        ))}
      </div>

      {(editingPerson || isCreating) && (
        <EditProfileForm
          person={editingPerson}
          onSubmit={handleSave}
          onClose={handleClose}
        />
      )}
    </div>
  );
}

export default CardProfile;
